import productsService from "../services/productsService";
import Utils from "../utils/Utils";

const util = new Utils();

class ProductController {
  static async getAllProducts(req, res) {
    try {
      const allProducts = await productsService.getAllProducts();
      if (allProducts.length > 0) {
        util.setSuccess(200, "Products retrieved", allProducts);
      } else {
        util.setSuccess(200, "No Product found");
      }
      return util.send(res);
    } catch (error) {
      util.setError(400, error);
      return util.send(res);
    }
  }

  static async addProduct(req, res) {
    if (
      !req.body.name ||
      !req.body.price ||
      !req.body.type ||
      !req.body.isExtras ||
      !req.body.hasOptions ||
      !req.body.createdAt ||
      !req.body.updatedAt
    ) {
      util.setError(400, "Please provide complete details");
      return util.send(res);
    }
    const newProduct = req.body;
    try {
      const createdProduct = await productsService.addProduct(newProduct);
      util.setSuccess(201, "Product Added!", createdProduct);
      return util.send(res);
    } catch (error) {
      util.setError(400, error.message);
      return util.send(res);
    }
  }

  static async updateProduct(req, res) {
    const alteredProduct = req.body;
    const { id } = req.params;
    if (!Number(id)) {
      util.setError(400, "Please input a valid numeric value");
      return util.send(res);
    }
    try {
      const updateProduct = await productsService.updateProduct(
        id,
        alteredProduct
      );
      if (!updateProduct) {
        util.setError(404, `Cannot find Product with the id: ${id}`);
      } else {
        util.setSuccess(200, "Product updated", updateProduct);
      }
      return util.send(res);
    } catch (error) {
      util.setError(404, error);
      return util.send(res);
    }
  }

  static async getProducts(req, res) {
    const { id } = req.params;

    if (!Number(id)) {
      util.setError(400, "Please input a valid numeric value");
      return util.send(res);
    }

    try {
      const theProduct = await productsService.getProduct(id);

      if (!theProduct) {
        util.setError(404, `Cannot find Product with the id ${id}`);
      } else {
        util.setSuccess(200, "Found Product", theProduct);
      }
      return util.send(res);
    } catch (error) {
      util.setError(404, error);
      return util.send(res);
    }
  }

  static async deleteProduct(req, res) {
    const { id } = req.params;

    if (!Number(id)) {
      util.setError(400, "Please provide a numeric value");
      return util.send(res);
    }

    try {
      const ProductToDelete = await productsService.deleteProduct(id);

      if (ProductToDelete) {
        util.setSuccess(200, "Product deleted");
      } else {
        util.setError(404, `Product with the id ${id} cannot be found`);
      }
      return util.send(res);
    } catch (error) {
      util.setError(400, error);
      return util.send(res);
    }
  }
}

export default ProductController;

import database from '../src/models';

class productsService {
  static async getAllProducts() {
    try {
      return await database.products.findAll();
    } catch (error) {
      throw error;
    }
  }

  static async addProduct(newProduct) {
    try {
      return await database.products.create(newProduct);
    } catch (error) {
      throw error;
    }
  }

  static async getProducts(id) {
    try {
      const theProducts = await database.products.findOne({
        where: { id: Number(id) }
      });

      return theProducts;
    } catch (error) {
      throw error;
    }
  }

  static async updateProduct(id, updateProduct) {
    try {
      const productToUpdate = await database.products.findOne({
        where: { id: Number(id) }
      })

      if (productToUpdate) {
        await database.products.update(updateProduct, { where: { id: Number(id) } })
        return updateProduct;
      }
      return null;
    } catch (error) {
      throw error;
    }
  }

  static async deleteProduct(id) {
    try {
      const productsToDelete = await database.products.findOne({
        where: { id: Number(id) }
      });

      if (productsToDelete) {
        const deletedProducts = await database.products.destroy({
          where: { id: Number(id) }
        });
        return deletedProducts;
      }
      return null;
    } catch (error) {
      throw error;
    }
  }
}

export default productsService;

'use strict';
require('dotenv').config();
import fs from 'fs';
import path from 'path';
import Sequelize from 'sequelize';
import configJson from '../config/config';

const basename = path.basename(__filename);
const env = process.env.NODE_ENV ? process.env.NODE_ENV : 'development';

const config = configJson[env];

const db = {};

let sequelize
if (config.environment === 'production') {
  sequelize = new Sequelize(process.env[config.use_env_variable], config);
  sequelize = new Sequelize(
    process.env.DB_NAME,
    process.env.DB_USER,
    process.env.DB_PASS, {
      host: process.env.DB_HOST,
      port: process.env.DB_PORT,
      dialect: 'postgres',
      dialectOption: {
        ssl: true,
        native: true
      },
      logging: true
    }
  )
} else {
  sequelize = new Sequelize(
     config.database, config.username, config.password, config
  )
}

fs
  .readdirSync(__dirname)
  .filter((file) => {
    return (file.indexOf('.') !== 0) &&
           (file !== basename) && (file.slice(-3) === '.js')
  })
  .forEach((file) => {
    const model = sequelize.import(path.join(__dirname, file))
    db[model.name] = model
  })

Object.keys(db).forEach((modelName) => {
  if (db[modelName].associate) {
    db[modelName].associate(db)
  }
})

db.sequelize = sequelize;
db.Sequelize = Sequelize;

export default db;

